package com.okbind;import android.view.View;import com.okbind.databinding.ItemMainBinding;import com.okbind.library.ItemViewBind;import com.okbind.library.OkViewHold;/** * Created by chen.huarong on 2019-09-10. */public class TestItembind extends ItemViewBind<TestViewModel> {    @Override    public void onBind(OkViewHold holder, int position, TestViewModel item) {        super.onBind(holder, position, item);        ItemMainBinding binding = holder.getItemBinding();        binding.setVariable(BR.viewModel, item);        binding.setVariable(BR.onClickListener, new View.OnClickListener() {            @Override            public void onClick(View v) {            }        });        binding.executePendingBindings();    }    @Override    public int getLayoutId(int viewType) {        return R.layout.item_main;    }}