package com.okbind;import com.okbind.databinding.ItemMain2Binding;import com.okbind.databinding.ItemMainBinding;import com.okbind.library.ItemViewBind;import com.okbind.library.OkViewHold;/** * Created by chen.huarong on 2019-09-10. */public class TestItembind2 extends ItemViewBind<TestViewModel2> {    @Override    public void onBind(OkViewHold holder, int position, TestViewModel2 item) {        super.onBind(holder, position, item);        ItemMain2Binding binding = holder.getItemBinding();        binding.setVariable(BR.viewModel, item);        binding.executePendingBindings();    }    @Override    public int getLayoutId(int viewType) {        return R.layout.item_main2;    }}